mapper.select:
SELECT id,
username,
ip,
address,
browser,
os,
status,
error_message,
type,
create_time
from boot_sys_login_log
where del_flag = 0

  and username like #{pageQuery.username}


  and ip like #{pageQuery.ip}


  and address like #{pageQuery.address}


  and browser like #{pageQuery.browser}


  and os like #{pageQuery.os}


  and error_message like #{pageQuery.errorMessage}


  and type = #{pageQuery.type}


  and status = #{pageQuery.status}


  and to_char(create_time, 'YYYY-MM-DD')  >=  #{pageQuery.params.startDate}


  and to_char(create_time, 'YYYY-MM-DD')  <=  #{pageQuery.params.endDate}

order by id desc
limit ${pageQuery.pageSize} OFFSET ${pageQuery.pageIndex}

SELECT count(1)
from boot_sys_login_log
where del_flag = 0

  and username like #{pageQuery.username}


  and ip like #{pageQuery.ip}


  and address like #{pageQuery.address}


  and browser like #{pageQuery.browser}


  and os like #{pageQuery.os}


  and error_message like #{pageQuery.errorMessage}


  and type = #{pageQuery.type}


  and status = #{pageQuery.status}


  and to_char(create_time, 'YYYY-MM-DD')  >=  #{pageQuery.params.startDate}


  and to_char(create_time, 'YYYY-MM-DD')  <=  #{pageQuery.params.endDate}

select version
from boot_sys_login_log
where id = #{id}

SELECT
username,
ip,
address,
browser,
os,
status,
error_message,
type,
create_time
from boot_sys_login_log
where del_flag = 0

  and username like #{pageQuery.username}


  and ip like #{pageQuery.ip}


  and address like #{pageQuery.address}


  and browser like #{pageQuery.browser}


  and os like #{pageQuery.os}


  and error_message like #{pageQuery.errorMessage}


  and type = #{pageQuery.type}


  and status = #{pageQuery.status}


  and to_char(create_time, 'YYYY-MM-DD')  >=  #{pageQuery.params.startDate}


  and to_char(create_time, 'YYYY-MM-DD')  <=  #{pageQuery.params.endDate}

order by id desc
