mapper.select:
select id,pid,`name`,icon,url,`type`,permission from boot_sys_menu where del_flag = 0
and `type` = #{type}
order by sort desc

SELECT boot_sys_menu.url, boot_sys_menu.type, boot_sys_menu.sort, boot_sys_menu.id, boot_sys_menu.pid, boot_sys_menu.name, boot_sys_menu.icon, boot_sys_menu.permission FROM boot_sys_menu ,boot_sys_role ,boot_sys_role_menu ,boot_sys_user ,boot_sys_user_role WHERE boot_sys_menu.id = boot_sys_role_menu.menu_id AND boot_sys_role_menu.role_id = boot_sys_role.id AND boot_sys_user.id = boot_sys_user_role.user_id AND boot_sys_role.id = boot_sys_user_role.role_id AND boot_sys_user.id = #{userId} and boot_sys_menu.del_flag = 0
and boot_sys_menu.type = #{type}
group by boot_sys_menu.url, boot_sys_menu.type, boot_sys_menu.sort, boot_sys_menu.id, boot_sys_menu.pid, boot_sys_menu.name, boot_sys_menu.icon, boot_sys_menu.permission order by boot_sys_menu.sort desc
