mapper.select:
SELECT id, save_cmd, modify_cmd, remove_cmd, page_qry, get_qry, import_cmd, export_cmd, convertor, save_cmd_exe, modify_cmd_exe, remove_cmd_exe, page_qry_exe, get_qry_exe, import_cmd_exe, export_cmd_exe, entity, service_i, service_impl, domain_service, data_object, gateway, gateway_impl, controller, mapper, mapper_xml, api, view, form_view, create_time from boot_generator_template where del_flag = 0
and to_char(create_time, 'YYYY-MM-DD') >= ${pageQuery.params.startDate}
and to_char(create_time, 'YYYY-MM-DD') <= ${pageQuery.params.endDate}
order by id desc limit ${pageQuery.pageSize} offset ${pageQuery.pageIndex}

		SELECT count(1)
		from boot_generator_template
		where del_flag = 0

            and to_char(create_time, 'YYYY-MM-DD')  >=  ${pageQuery.params.startDate}


            and to_char(create_time, 'YYYY-MM-DD')  <=  ${pageQuery.params.endDate}

select version
from boot_generator_template
where id = #{id}
